import React, { useEffect, useState } from 'react';

const BatteryStatus = () => {
  const [batteryLevel, setBatteryLevel] = useState(null);
  const [isCharging, setIsCharging] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  useEffect(() => {
    if ('getBattery' in navigator) {
      navigator.getBattery().then(function (battery) {
        updateBatteryStatus(battery);

        battery.addEventListener('levelchange', () => {
          updateBatteryStatus(battery);
        });

        battery.addEventListener('chargingchange', () => {
          updateBatteryStatus(battery);
        });
      });

      // Refresh the battery status every second
      const intervalId = setInterval(() => {
        navigator.getBattery().then(function (battery) {
          updateBatteryStatus(battery);
        });
      }, 1000);
      return () => {
        clearInterval(intervalId);
      };
    } else {
      console.log('Battery API not supported in this browser.');
    }
  }, []);



  const updateBatteryStatus = (battery) => {
    const level = battery.level * 100;
    setBatteryLevel(level);

    const charging = battery.charging;
    setIsCharging(charging);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const renderBatteryLogo = () => {
    const batteryWidth = `${batteryLevel}%`;
    const batteryColor = isCharging ? 'green' : 'black';

    return (

        <div
          style={{
            height: '100%',
            width: batteryWidth,
            backgroundColor: batteryColor,
          }}
        />

    );
  };

  return (
    <div>
      <button onClick={toggleDropdown}>
      {renderBatteryLogo()} {isDropdownOpen ? '▲' : '▼'}
      </button>
      {isDropdownOpen && (
        <div>
          <h3>Battery Status</h3>
          {batteryLevel !== null && (
            <div>

              <p>Battery Level: {batteryLevel}%</p>
            </div>
          )}
          {isCharging !== null && (
            <p>Charging: {isCharging ? 'Yes' : 'No'}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default BatteryStatus;
