import React, { useState,useEffect,useRef } from 'react'
import "./navbar.css";
import io from './icon/control-center.png'
import siri from './icon/siri.png'
import Dock from './Dock'
import Clock from './Clock';






export default function Navbar() {
  const [dropdowns, setDropdowns] = useState({
    apple: false,
    finder: false,
    file: false,
    edit: false,
    view: false,
    go: false,
    window: false,
    help: false,
    wifi: false,
    search: false,
    controlCentre: false,
  });



  const toggleDropdown = (dropdown) => {
    setDropdowns((prevState) => ({
      ...prevState,
      [dropdown]: !prevState[dropdown],
    }));
    setDropdowns((prevState) => ({
      ...prevState,
      apple: false,
      finder: false,
      file: false,
      edit: false,
      view: false,
      go: false,
      window: false,
      help: false,
      wifi: false,
      search: false,
      controlCentre: false,
    }));

    const closeDropdowns = () => {
      setDropdowns((prevState) => ({
        ...prevState,
        apple: false,
        finder: false,
        file: false,
        edit: false,
        view: false,
        go: false,
        window: false,
        help: false,
        wifi: false,
        search: false,
        controlCentre: false,
      }));
    };

  };
  const [batteryLevel, setBatteryLevel] = useState(null);
  const [isCharging, setIsCharging] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const togglebattery = () => {
    setIsDropdownOpen(!isDropdownOpen);

  };


  const navbarRef = useRef(null);

  const handleClickOutside = (event) => {
    if (navbarRef.current && !navbarRef.current.contains(event.target)) {
      closeDropdowns();
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);


  useEffect(() => {
    if ('getBattery' in navigator) {
      navigator.getBattery().then(function (battery) {
        updateBatteryStatus(battery);

        battery.addEventListener('levelchange', () => {
          updateBatteryStatus(battery);
        });

        battery.addEventListener('chargingchange', () => {
          updateBatteryStatus(battery);
        });
      });

      const intervalId = setInterval(() => {
        navigator.getBattery().then(function (battery) {
          updateBatteryStatus(battery);
        });
      }, 1000);
      return () => {
        clearInterval(intervalId);
      };
    } else {
      console.log('Battery API not supported in this browser.');
    }
  }, []);
  const [checkValue ,setCheckValue] =useState(false);
  const [wifiInfo,setWifiInfo] = useState(false);
  const WifiOn = ()=>{
    if(checkValue==false) {
      setCheckValue(true);
      setWifiInfo(!wifiInfo)
  }
  else{
    setCheckValue(false);
    setWifiInfo(false);
  }
  }
  const updateBatteryStatus = (battery) => {
    const level = Math.round(battery.level*100);
    setBatteryLevel(level);

    const charging = battery.charging;
    setIsCharging(charging);
  };

  const renderBatteryLogo = () => {
    const batteryWidth = `${batteryLevel}%`;
    const batteryColor = 'white';
    const chargingSymbol = isCharging ? (
      <i className="fa fa-bolt" style={{ fontSize: '18px', WebkitTextStroke: '0.8px black', color: 'white', position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)' }} />
    ) : null;


    return (
      <div style={{ display: 'flex', alignItems: 'center', position: 'relative' }}>
      <div
        style={{
          border: '2px solid rgb(112, 110, 130)',
          WebkitTextStroke: '1px black',
          width: '22px',
          height: '10px',
          borderRadius: '0.2rem',
        }}
      >
        <div
          style={{
            height: '70%',
            width: batteryWidth,
            backgroundColor: batteryColor,
            padding: '1px',
            borderRadius: '0.1rem',
          }}
        />
      </div>
      {chargingSymbol}
    </div>
    );
  };

  return (


 <body>
   <div className='nav' ref={navbarRef}></div>
 <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
    <div className='nav'>
      <div className="left">
      <a onClick={() => toggleDropdown('apple')}><i className="fa fa-apple"></i></a>
      <a onClick={() => toggleDropdown('finder')}><strong>Finder</strong></a>
      <a onClick={() => toggleDropdown('file')}>File</a>
      <a onClick={() => toggleDropdown('edit')}>Edit</a>
      <a onClick={() => toggleDropdown('view')}>View</a>
      <a onClick={() => toggleDropdown('go')}>Go</a>
      <a  onClick={() => toggleDropdown('window')}>Window</a>
      <a onClick={() => toggleDropdown('help')}>Help</a>
      </div>
      <div className="right">
        <a onClick={togglebattery} >{renderBatteryLogo()}</a>
      <a className='wifi'onClick={() => toggleDropdown('wifi')} ><i className='fa fa-wifi'></i></a>
      <a onClick={() => toggleDropdown('search')}><i className='fa fa-search'></i></a>
      <a onClick={() => toggleDropdown('controlCentre')}><img className='controlcentre' src={io}/></a>
      <a><img className='siri' src={siri}/></a>
      <a><strong><Clock/></strong></a>
      </div>
    </div>
    f
  {dropdowns.apple && (
            <div className="dropdown-content-Apple">
              <a className='ATM'>About This Mac</a>
              <a>System Settings</a>
              <a className='ATM'>App Store</a>
              <a className='ATM' >Recent Items</a>
              <a className='ATM'>Force Quit...</a>
              <a>Sleep</a>
              <a>Restart...</a>
              <a className='ATM'>Shut Down...</a>
              <a>Lock Screen</a>
              <a className='ATM'>Log Out User...</a>
            </div>
          )}
  {dropdowns.finder && (
          <div className="dropdown-content-Finder">
            <a className='ATM'>About Finder</a>
            <a className='ATM'>Settings...</a>
            <a className='ATM'>Empty Bin...</a>
            <a className='ATM'>Services</a>
            <a>Hide Finder</a>
            <a>Hide Others</a>
            <a className="show">Show All</a>
          </div>
          )}
  {dropdowns.file && (
          <div className="dropdown-content-File">
              <a>New Finder Window</a>
              <a>New Folder</a>
              <a className="show">New Folder with Selection</a>
              <a>New Smart Folder</a>
              <a>New Tab</a>
              <a className="show">Open</a>
              <a className="show">Open With</a>
              <a className='ATM'>Close Window</a>
              <a>Get Info</a>
              <a className="show">Rename</a>
              <a className="show">Compress</a>
              <a className="show">Duplicate</a>
